var net = require('net');
var buffer = require('buffy');

var socket = net.createConnection(5555, '192.168.1.1');
var fs = require('fs');

var file = fs.createWriteStream('pave.bin');
socket.pipe(file);

var size = 0;
socket.on('data', function(buffer) {
  size += buffer.length;
  console.log(size);

  if (size >= 1024 * 1024) {
    socket.end();
  }
});
