var PaVEParser = require('../lib/video/PaVEParser');
var fs         = require('fs');

var input = fs.createReadStream('pave.bin');
var output = fs.createWriteStream('video.h264');
var parser = new PaVEParser();

input.pipe(parser, {end: false});

parser.on('data', function(frame) {
  if (frame.signature != 'PaVE') {
    return;
  }

  output.write(frame.payload);
});
